import { Badge } from 'primereact/badge';
import { Component, useRef } from 'react';
import { Knob } from 'primereact/knob';
import { SpeedDial } from 'primereact/speeddial';
import { Toast } from 'primereact/toast';

export class HomeComponent extends Component {

    constructor(props) {
        super(props);
        this.showSuccess = this.showSuccess.bind(this);
        this.showInfo = this.showInfo.bind(this);
        this.showWarn = this.showWarn.bind(this);
        this.showError = this.showError.bind(this);
        this.showTopLeft = this.showTopLeft.bind(this);
        this.showBottomLeft = this.showBottomLeft.bind(this);
        this.showBottomRight = this.showBottomRight.bind(this);
        this.showMultiple = this.showMultiple.bind(this);
        this.showSticky = this.showSticky.bind(this);
        this.showConfirm = this.showConfirm.bind(this);
        this.clear = this.clear.bind(this); this.items = [
            {
                label: 'Add',
                icon: 'pi pi-pencil',
                command: () => {
                    this.toast.current.show({ severity: 'info', summary: 'Add', detail: 'Data Added' });
                }
            },
            {
                label: 'Update',
                icon: 'pi pi-refresh',
                command: () => {
                    this.toast.current.show({ severity: 'success', summary: 'Update', detail: 'Data Updated' });
                }
            },
            {
                label: 'Delete',
                icon: 'pi pi-trash',
                command: () => {
                    this.toast.current.show({ severity: 'error', summary: 'Delete', detail: 'Data Deleted' });
                }
            }
        ];
        this.basicData = {
            labels: ['January', 'February', 'March', 'April', 'May', 'June', 'July'],
            datasets: [
                {
                    label: 'STEPS',
                    backgroundColor: '#42A5F5',
                    data: [65, 59, 80, 81, 56, 55, 40]
                },
                {
                    label: 'CALORIES',
                    backgroundColor: '#FFA726',
                    data: [28, 48, 40, 19, 86, 27, 90]
                }
            ]
        };
    }
    render() {
        const {items} = this.items;
        return ( <div className="p-fluid p-grid p-jc-center p-col">
         <Toast ref={toastRef} />

<div className="card p-shadow-3 p-col-3">
<h3>STEPS</h3>            
<Knob value={2574} min={0} max={7000}  />
<Badge value="2" className="p-mr-2"></Badge>
                </div>

                <div className="card p-shadow-3 p-col-3">
                <h3>CALORIES (KCAL)</h3>            
<Knob value={127} min={0} max={500}  />
<Badge value="5" className="p-mr-2"></Badge>
                </div>
                <div className="card p-shadow-3 p-col-3">
<h3>DISTANCE (KM)</h3>            
<Knob value={2.35} min={0} max={10}  />
<Badge value="1" className="p-mr-2"></Badge>
                </div>

                <div className="card p-shadow-3 p-col-3">
                <h3>BPM</h3>            
<Knob value={95} min={0} max={120}  />
<Badge value="8" className="p-mr-2"></Badge>
                </div>
                <SpeedDial model={items} />
               </div>

                );
    }
}
