import React, { useState, useRef } from 'react';

import { InputText } from 'primereact/inputtext';
import { Button } from 'primereact/button';
import { SelectButton } from 'primereact/selectbutton';
import { Toast } from 'primereact/toast';
import { DataTable } from 'primereact/datatable';
import { Column } from 'primereact/column';
import { Dialog } from 'primereact/dialog';
import PrimeReact from 'primereact/api';

import 'primereact/resources/themes/saga-blue/theme.css';
import 'primereact/resources/primereact.min.css';
import 'primeicons/primeicons.css';
import 'primeflex/primeflex.css';

import logo from './logo.svg';
import './App.css';

function App() {
  const [name, setName] = useState('');
  const [surname, setSurname] = useState('');
  const [phone, setPhone] = useState('');
  const [email, setEmail] = useState('');
  const [gender, setGender] = useState('');
  const toastRef = useRef();
  const options = ['MALE', 'FEMALE'];
  let users = [{name: name, surname: surname, phone: phone, email: email, gender: gender}];
  // active ripple effect
  PrimeReact.ripple = true;

  const onFormSubmit = (e) => {
    if (name && surname && phone && email) {
      toastRef.current.show({ severity: 'info', summary: name + ' ' + surname + ' ' + phone + ' ' + email , life: 3000 });
    }
   users = [{name: name, surname: surname, phone: phone, email: email, gender: gender}];


    e.preventDefault();
  }

  return (
    <div className="App">

      <Toast ref={toastRef} />

      <header className="App-header">
        <img src={logo} className="App-logo" alt="logo" />
      </header>

      <form className="p-d-flex p-jc-center p-mt-6" onSubmit={onFormSubmit}>
      <div className="p-fluid p-grid p-col-4">
      <div className="p-field p-col-12">
                                <span className="p-float-label p-input-icon-left">
                                    <i className="pi pi-user" />
                                    <InputText inputId="name" label="Name" value={name} onChange={(e) => setName(e.target.value)} />
        <label htmlFor="name">Name</label>
        </span>
        </div>

        <div className="p-field p-col-12">
                                <span className="p-float-label p-input-icon-left">
                                    <i className="pi pi-user" />
                                    <InputText inputId="surname" value={surname} onChange={(e) => setSurname(e.target.value)} />
                               <label htmlFor="surname">Surname</label>
        </span>
        </div>
        <div className="p-field p-col-12">
                                <span className="p-float-label p-input-icon-left">
                                    <i className="pi pi-phone" />
                                    <InputText inputId="phone" label="Phone" value={phone} onChange={(e) => setPhone(e.target.value)} />
        <label htmlFor="name">Phone</label>
        </span>
        </div>
        <div className="p-field p-col-12">
                                <span className="p-float-label p-input-icon-left">
                                    <i className="pi pi-envelope" />
                                    <InputText inputId="email" value={email} onChange={(e) => setEmail(e.target.value)} />
                               <label htmlFor="email">Email</label>
        </span>
        </div>
        <div className="p-field p-col-12">
                                <span className="p-input-icon-left">

                                 <label className="" htmlFor="gender"> Gender</label>


         <SelectButton id="gender" value={gender} options={options} onChange={(e) => setGender(e.target.value)} />
        </span>
</div>

        <Button type="submit" label="Submit" icon="pi pi-check" onClick={() => onClick('displayBasic')} className="p-ml-2"/>
      </div>
      </form>
      <div>
      <Dialog header="Header" style={{ width: '50vw' }} onHide={() => onHide('displayBasic')}>
<div className="card">
                <DataTable value={users}>
                    <Column field="name" header="Name"></Column>
                    <Column field="surname" header="Surname"></Column>
                    <Column field="phone" header="Phone"></Column>
                    <Column field="email" header="Email"></Column>
                    <Column field="gender" header="Gender"></Column>
                </DataTable>
            </div>
            </Dialog>
        </div>
    </div>
  );

}

export default App;
