import { Badge } from 'primereact/badge';
import { Component, useState } from 'react';
import { Knob } from 'primereact/knob';
import { Chart } from 'primereact/chart';

export class HomeComponent extends Component {
    constructor(props) {
        super(props);

        this.basicData = {
            labels: ['January', 'February', 'March', 'April', 'May', 'June', 'July'],
            datasets: [
                {
                    label: 'My First dataset',
                    backgroundColor: '#42A5F5',
                    data: [65, 59, 80, 81, 56, 55, 40]
                },
                {
                    label: 'My Second dataset',
                    backgroundColor: '#FFA726',
                    data: [28, 48, 40, 19, 86, 27, 90]
                }
            ]
        };
        this.options = this.getBasicOptions();


    }

    getBasicOptions() {

        let basicOptions = {
            maintainAspectRatio: false,
            aspectRatio: .8,
            plugins: {
                legend: {
                    labels: {
                        color: '#495057'
                    }
                }
            },
            scales: {
                x: {
                    ticks: {
                        color: '#495057'
                    },
                    grid: {
                        color: '#ebedef'
                    }
                },
                y: {
                    ticks: {
                        color: '#495057'
                    },
                    grid: {
                        color: '#ebedef'
                    }
                }
            }
        };
        return basicOptions;
    }
    render() {
        const { basicOptions } = this.options;
        return ( <div className="p-fluid p-grid p-jc-center p-col">

<div className="card p-shadow-3 p-col-3">
<h3>STEPS</h3>            
<Knob value={2574} min={0} max={7000}  />
<Badge value="2" className="p-mr-2"></Badge>
                </div>

                <div className="card p-shadow-3 p-col-3">
                <h3>CALORIES (KCAL)</h3>            
<Knob value={127} min={0} max={500}  />
<Badge value="5" className="p-mr-2"></Badge>
                </div>
                <div className="card p-shadow-3 p-col-3">
<h3>DISTANCE (KM)</h3>            
<Knob value={2.35} min={0} max={10}  />
<Badge value="1" className="p-mr-2"></Badge>
                </div>

                <div className="card p-shadow-3 p-col-3">
                <h3>BPM</h3>            
<Knob value={95} min={0} max={120}  />
<Badge value="8" className="p-mr-2"></Badge>
                </div>

                <h5>Vertical</h5>
                    <Chart type="bar" data={this.basicData} options={basicOptions} />
                </div>        



                );
    }
}
