import React, { useState, useRef } from 'react';

import { InputText } from 'primereact/inputtext';
import { Button } from 'primereact/button';
import { Toast } from 'primereact/toast';
import PrimeReact from 'primereact/api';

import 'primereact/resources/themes/saga-blue/theme.css';
import 'primereact/resources/primereact.min.css';
import 'primeicons/primeicons.css';
import 'primeflex/primeflex.css';

import logo from './logo.svg';
import './App.css';

function App() {
  const [name, setName] = useState('');
  const [surname, setSurname] = useState('');
  const toastRef = useRef();

  // active ripple effect
  PrimeReact.ripple = true;

  const onFormSubmit = (e) => {
    if (name && surname) {
      toastRef.current.show({ severity: 'info', summary: name + ' ' + surname, life: 3000 });
    }

    // clear
    setName('');
    setSurname('');

    e.preventDefault();
  }

  return (
    <div className="App">

      <Toast ref={toastRef} />

      <header className="App-header">
        <img src={logo} className="App-logo" alt="logo" />
      </header>

      <form className="p-d-flex p-jc-center p-mt-6" onSubmit={onFormSubmit}>
      <div className="p-fluid p-grid">
                          <div className="p-field p-col-12 p-md-4">
                             <span className="p-float-label">
        <InputText inputId="name" label="Name" value={name} onChange={(e) => setName(e.target.value)} />
        <label htmlFor="name">Name</label>
        </span>
                             <span className="p-float-label">
                               <InputText inputId="surname" value={surname} onChange={(e) => setSurname(e.target.value)} />
                               <label htmlFor="surname">Surname</label>
        </span>
        </div>
        <div className="p-field p-col-12 p-md-4">
                             <span className="p-float-label">
        <InputText inputId="name" label="Name" value={name} onChange={(e) => setName(e.target.value)} />
        <label htmlFor="name">Name</label>
        </span>
        </div>
        <div className="p-field p-col-12 p-md-4">
                             <span className="p-float-label">
                               <InputText inputId="surname" value={surname} onChange={(e) => setSurname(e.target.value)} />
                               <label htmlFor="surname">Surname</label>
        </span>
        </div>
        <Button type="submit" label="Submit" icon="pi pi-check" className="p-ml-2"/>
      </div>
      </form>
    </div>
  );
}

export default App;
